mapper.select:
SELECT wr_uid "uid", wr_subject subject, wr_content content, wr_name name, wr_viewcnt viewCnt, wr_regdate regDate
FROM test_write 
WHERE wr_uid = #{param1}

mapper.update:
UPDATE test_write 
SET wr_viewcnt = wr_viewcnt + 1 
WHERE wr_uid = #{param1}

UPDATE test_write 
SET wr_subject = #{param2} , wr_content = #{param3} 
WHERE wr_uid = #{param1}
